import React from "react";

export default class Planets extends Component {

  state = {
    hasErrors: false,
    films: {}
  };

  componentWillUnMount() {
    fetch("https://swapi.co/api/films/")
      .then(response => {
        return response.json();
      }).then(data => {
        this.setState({  
            films: data
        })
      }).catch(() => this.setState({ hasErrors: true }));

  }



}
