import React, { Component } from "react";

export default class Planets extends Component {
  state = {
    hasErrors: false,
    films: {},
    movies:{}
  };

  componentDidMount() {
    fetch("https://swapi.co/api/films/")
      .then(response => {
        return response.json();
      }).then(data => {
        let name = data.results;
        this.setState({  
            films: name,
        })
      }).catch(() => this.setState({ hasErrors: true }));
  }


  render() {
    const { movies } = this.state.films;
    <ul>
    return({ movies.length && movies.map(m => <li key={m.title}>{m.title}</li>) });
    </ul>
  }
}
