import React, { Component } from "react";

export default class Planets extends Component {
  state = {
    hasErrors: false,
    films: {},
    movies:{}
  };

  componentDidMount() {
    fetch("https://swapi.co/api/films/")
      .then(response => {
        return response.json();
      }).then(data => {
        this.setState({  
            films: data.results.title,
        })
      }).catch(() => this.setState({ hasErrors: true }));
  }



  render() {
    return <div className="colorLetra">{JSON.stringify(this.state.films)}</div>;
  }
}
