import React, { Component } from "react";

export default class Planets extends Component {
  state = {
    hasErrors: false,
    films: {}
  };

  componentDidMount() {
    fetch("https://swapi.co/api/films/")
      .then(res => {
        return res.json();
      }).then(res => {
        this.setState({  
            films: res.results,
            nameTitle: res.title,

        })
      })
      .catch(() => this.setState({ hasErrors: true }))
      ;
  }



  render() {
    return <div className="colorLetra">{JSON.stringify(this.state.nameTitle)}</div>;
  }
}
