import React, { Component } from "react";

export default class Planets extends Component {

  state = {
    hasErrors: false,
    films: {}
  };

  useEffect = () => {
    fetch("https://swapi.co/api/films/")
    .then(response => {
      return response.json();
    }).then(data => {


        const datas = data.results;
        console.log(datas);


      this.setState({  
          films: data.results,
      })
    }).catch(() => this.setState({ hasErrors: true }));

  }








  render() {
    this.state.films.map((results, index) => {

        return (<p key={index}>Hello, {results.name} from {results.country}!</p>);
      });
  }
}
