import React, { Component } from "react";
import MovieTitle from '../components/MovieTitle';
import MovieData from '../components/MovieData';
import './styles.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
const API_URL = 'https://swapi.co/api/films/';


class App extends Component {
  state = {
    users: []
  }
  componentDidMount() {
    const url = 'https://swapi.co/api/films/';
    axios.get(url).then(response => response.data)
    .then((data) => {

      this.setState({ users: data.results })
     })
  }

  render() {

    const data = {
      nameTitles: "Star Wars - A New Hope",
      directors: "George Lucassss",
      episode_ids: 4,
  }

    return (
      <div className="mt-5">
      <div className="row">
      {this.state.users.map((user) => (
          <div className="elemto col-md-12 shadow p-3 mb-5 bg-white rounded">
          <div className="col-md-12">
              <h1><MovieTitle nameTitle={user.title}></MovieTitle></h1>
          </div>
          <div className="col-md-12">
              <span><MovieData director={user.directors} episode_id={user.episode_ids}></MovieData></span>
          </div>
      </div>  
        ))}
      </div> 
      <img className="naveDos animated fadeInDown" src="nave.png"></img>
      <img className="naveUno animated fadeInDown" src="halcon.png"></img>
  </div>

    );
  }
}
export default App;







